async fn get_all_host_configs_benchmark() {
    // Start an async mock server
    let server = MockServer::start_async().await;

    // Mock the POST /rest/login endpoint
    server
        .mock_async(|when, then| {
            when.method(POST).path("/rest/login");
            then.status(200)
                .header("Content-Type", "application/json")
                .body(r#"{"token": "some_auth_token"}"#);
        })
        .await;

    // Mock the GET /rest/config/host endpoint
    server
        .mock_async(|when, then| {
            when.method(GET).path("/rest/config/host");
            then.status(200)
                .header("Content-Type", "application/json")
                .body(ALL_HOST_CONFIGS);
        })
        .await;

    let ov = OpsviewClient::builder()
        .url(&server.base_url())
        .username("username")
        .password("password")
        .ignore_cert(false)
        .build()
        .await
        .unwrap();

    ov.get_all_host_configs(None).await.unwrap();

    // Optionally, verify that the mocks were called
    // server.verify(); // Uncomment if you wish to verify that the mocks were hit
}

fn criterion_benchmark(c: &mut Criterion) {
    // Create a single Tokio runtime for the benchmarks
    let runtime = tokio::runtime::Runtime::new().unwrap();
    let mut group = c.benchmark_group("OpsviewClient Benchmarks");

    group.bench_function("get_all_host_configs", |b| {
        b.to_async(&runtime).iter(get_all_host_configs_benchmark)
    });

    group.finish();
}

criterion_group!(benches, criterion_benchmark);
criterion_main!(benches);
